mapper.select:
SELECT b.board_no, b.member_no, b.origin_content, b.trans_content, b.tag, b.like_cnt, b.view_cnt, b.board_public, b.reply_public, b.report_cnt, b.write_dt, b.update_dt, m.name, m. filename, i.img_no, i.img_filename FROM aim_board b INNER JOIN aim_member m ON b.member_no = m.member_no INNER JOIN aim_generated_img i ON b.board_no = i.board_no WHERE (select count(range_no) from aim_hide_setting hs where hs.no='5' and hs.member_no=m.member_no and range_no=2) != 1
AND (b.tag LIKE CONCAT('%', #{keyword}, '%') OR b.origin_content LIKE CONCAT('%', #{keyword}, '%')) AND b.board_public = 1
ORDER BY RAND();
